<template>
    <div ref="chart"></div>
</template>
<script>
let whiteSrc =
  "data:image/png;base64,";
let whiteImg = new Image();
whiteImg.src = whiteSrc;

export default {
  name: "EquipmentChart",
  props: {
    info: {
      type: Object,
      default: function() {
        return {
          dataAxis: ["一月", "二月", "三月", "四月"],
          data: [220, 182, 191, 234],
          maxdata: [500, 500, 500, 500]
        };
      }
    },
    size: {
      type: Object,
      default: function() {
        return {
          width: 450,
          height: 300
        };
      }
    },
    position: {
      type: Boolean,
      default: true
    }
  },
  computed: {
    positionSet() {
      return this.position;
    },
    dataAxis() {
      return this.info.dataAxis;
    },
    data() {
      return this.info.data;
    },

    mymaxData() {
      return this.info.maxdata;
    },

    width() {
      return this.size.width + "px";
    },
    height() {
      return this.size.height + "px";
    },
    options() {
      var obj = {
        grid: { x: "10%", y: "2%" },
        xAxis: {
          data: this.dataAxis,
          position: this.positionSet ? "bottom" : "top", //设置出现的位置
          axisTick: {
            // 多少个单元 出现一个锯齿
            show: false,
            interval: 0
          },
          axisLine: {
            //设置第一条线
            lineStyle: {
              color: "rgba(255,255,255,0.2)" //设置 x 轴的颜色 第一条线
            }
          },
          splitLine: {
            //设置虚线
            // show: false,
            // interval: 0,//设置虚线
            // lineStyle: {
            //     type: 'dashed',
            //     color: 'rgba(255,255,255,0.4)'
            // }
          },
          axisLabel: {
            //设置 字体颜色
            textStyle: {
              color: "#dcdcdc" //y轴字体的颜色
            }
          }
        },
        yAxis: {
          splitLine: {
            show: false, //等分线的设置
            lineStyle: {
              color: "rgba(255,255,255,0.2)"
            }
          },
          axisLine: {
            show: false,
            lineStyle: {
              color: "rgba(255,255,255,0.2)" //设置 y 轴的颜色 第一条线
            }
          },
          axisTick: {
            show: false
          },
          axisLabel: {
            textStyle: {
              color: "#dcdcdc" //y轴字体的颜色
            }
          }
        },
        dataZoom: [
          {
            type: "inside"
          }
        ],
        series: [
          {
            // For shadow
            type: "bar",
            itemStyle: {
              normal: { color: "rgba(60,48,38,0.6)" }
            },
            barGap: "-100%",
            barCategoryGap: "40%",
            barWidth: 20,
            data: this.mymaxData,
            animation: false
          },
          {
            type: "bar",
            barWidth: 20,
            itemStyle: {
              normal: {
               // barBorderRadius: [2, 2, 0, 0]
                color: {
                  image: whiteImg,
                  repeat: "repeat"
                }
              }
            },
            data: this.data
          }
        ]
      };
      return obj;
    }
  },
  mounted() {
    this.$nextTick(() => {
      setTimeout(this.setChart, 2000);
      //this.setChart()
    });
  },
  watch: {
    data: function() {
      this.setChart();
    }
  },
  methods: {
    setChart() {
      var dom = this.$refs.chart;
      dom.style.width = this.width;
      dom.style.height = this.height;
      var myChart = this.$echarts.init(dom);
      myChart.setOption(this.options);
    }
  }
};
</script>

